  constructor(props) {
    super(props);
    this.state = {
        selectedFile : null ,   //mon fichier est stoker ici//
        dataFromBack  : [],
        selectedCheckbox : [],
        value : 1
    };
}
    onChangeHandler = e => {
    this.setState({
    selectedFile : e.target.files,
    })
    console.log(e.target.files[0]);
