            console.log("res.data du BACK", res.data)
            this.setState({
            dataFromBack : res.data[0].resulJson
            })      
        })
    }

    handleChange = (e) => {
        if(e.target.value ){
            const testTab = [];
            testTab.push(this.state.dataFromBack[e.target.value])
          this.setState({
            selectedCheckbox : testTab , 
            value : e.target.value
          }) 
        }
    }

