        const data = new FormData()
        for (let i = 0 ; i < this.state.selectedCheckbox.length ; i++){
          const coordonnanceDataBackFront = this.state.dataFromBack[this.state.selectedCheckbox[i]]
          data.append('radio', coordonnanceDataBackFront) //recuperer la donnée de fromback et la checkbok l'indice de validation
            }
        await axios.post("http://localhost:3000/upload", data , {
        })

    }

    drawLine = () => {
        let result = [];
