                    <th>
                    <Radio.Group value={this.state.value}>
                        <Radio onChange={this.handleChange} value={i}></Radio>
                    </Radio.Group></th>
                    </tr>)
                }
            })
        }
        return result
    }

    render() {
        console.log(" Le state" , this.state.dataFromBack)
        console.log('INPUT' ,this.state.selectedCheckbox);
        const Test = '' ; 
        if(this.state.selectedCheckbox[0]){
            const Test = this.state.selectedCheckbox[0][diag];
            console.log(Test ,'CHAMMMPPPPPPPP');
        }
    return (
        <div className="container">
            <div className="">
            <div className="">
                <form method="post" action="#" id="#">
                    <div className="form-group files">
                        <input id=''type="file" name="file" multiple onChange={this.onChangeHandler}/>
                    </div>
                </form>
                <button type="button" className="btn" onClick={this.onClickHandler}>Upload</button>
                <br/>
                <br/>
                <br/>
                <table className="table">
                <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Diagnostic</th>
                    <th scope="col">Score</th>
                    <th scope="col">Validation</th>
                </tr>
                </thead>
                  <tbody>
                      {this.drawLine()}
                  </tbody> 
            </table>
            <div className="Wrapper">
            <div className="Input">

                <input type="text" id="input" className="Input-text" placeholder={Test}/>
                <label htmlFor="input" className="Input-label">Modification</label>
            </div>
        </div>
            </div>
            </div> 
        </div>
    );
  }
}


