import React from 'react';
import { Radio } from 'antd';
import axios from 'axios';



class Upload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        selectedFile : null ,   //mon fichier est stoker ici//
        dataFromBack  : [],
        selectedCheckbox : [1],
    };
}
    onChangeHandler = e => {
    this.setState({
    selectedFile : e.target.files,
    })
    console.log(e.target.files[0]);
    }

    onClickHandler = async () => {
        const data = new FormData() 
        for(var i = 0 ; i < this.state.selectedFile.length ; i++)
        { //permet de choisir plusieurs fichier //
            data.append('file', this.state.selectedFile[i]) //envoie data back //
        }
        await axios.post("http://localhost:3000/upload", data, { 
        })
        .then(res => { //data du back //
            console.log("res.data du BACK", res.data)
            this.setState({
            dataFromBack : res.data[0].resulJson
            })      
        })
    }

    handleChange = (e) => {
        if(e.target.value ){
            this.state.selectedCheckbox.push(this.state.dataFromBack[e.target.value])
          this.setState({
            selectedCheckbox : [...this.state.selectedCheckbox]
          })
        }
    }

    sendFileBack  = async () => {
        const data = new FormData()
        for (let i = 0 ; i < this.state.selectedCheckbox.length ; i++){
          const coordonnanceDataBackFront = this.state.dataFromBack[this.state.selectedCheckbox[i]]
          data.append('radio', coordonnanceDataBackFront) //recuperer la donnée de fromback et la checkbok l'indice de validation
            }
        await axios.post("http://localhost:3000/upload", data , {
        })

    }

    drawLine = () => {
        let result = [];
        if(this.state.dataFromBack){
            this.state.dataFromBack.map((row,i) => {
                console.log(row)
                if(i > 0){
                result.push( <tr key = {i}>
                    <th scope="row">{i}</th>
                    <th>{row["diag"]}</th>
                    <th>{row["scores"]}</th>
                    <th>
                    <Radio.Group onChange={this.handleChange} value={this.state.selectedCheckbox}>
                        <Radio value={i}></Radio>
                    </Radio.Group></th>
                    </tr>)
                }
            })
        }
        return result
    }

    render() {
        console.log(" Le state" , this.state.dataFromBack)
        console.log('INPUT' ,this.state.selectedCheckbox);

    return (
        <div className="container">
            <div className="">
            <div className="">
                <form method="post" action="#" id="#">
                    <div className="form-group files">
                        <input id=''type="file" name="file" multiple onChange={this.onChangeHandler}/>
                    </div>
                </form>
                <button type="button" className="btn" onClick={this.onClickHandler}>Upload</button>
                <br/>
                <br/>
                <br/>
                <table className="table">
                <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Diagnostic</th>
                    <th scope="col">Score</th>
                    <th scope="col">Validation</th>
                </tr>
                </thead>
                  <tbody>
                      {this.drawLine()}
                  </tbody> 
            </table>
            </div>
            </div> 
        </div>
    );
  }
}


export default Upload;
